var exec = require('child_process').exec;

module.exports = function(comando){
	return {
		abrir: function(){
			var cmd = "\"" + comando + "\"";
			exec(cmd, function(error, stdout, stderr){
				console.log(stdout);
				console.log(error);
				console.log(stderr);
			});
		},
		executar: function(){
			exec(comando, function(error, stdout, stderr){
				console.log(stdout);
				console.log(error);
				console.log(stderr);
			});
		},
	}
}